mapper.select:
SELECT *
FROM kk_order
WHERE 1=1

	AND playmate_name LIKE CONCAT('%', #{playmateName}, '%')


	AND boss_account LIKE CONCAT('%', #{bossAccount}, '%')


	AND order_status = #{orderStatus}


	AND order_time >= #{startTime}


	AND order_time <= #{endTime}

AND is_delete = 0
ORDER BY order_time DESC
